import React, { useEffect, useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix the default icon issue with Leaflet in React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png'
});

const WebSocketMap = () => {
  const [data, setData] = useState([]);
  const socketRef = useRef(null);

  useEffect(() => {
    const connectWebSocket = () => {
      socketRef.current = new WebSocket('ws://localhost:3002');

      socketRef.current.onopen = () => {
        console.log('Connected to WebSocket server');
      };

      socketRef.current.onmessage = (event) => {
        const receivedData = JSON.parse(event.data);
        console.log('Received data:', receivedData);
        setData(receivedData);
      };

      socketRef.current.onclose = (event) => {
        console.log('Disconnected from WebSocket server', event);
        // Reconnect after a short delay
        setTimeout(connectWebSocket, 1000);
      };

      socketRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    };

    connectWebSocket();

    return () => {
      if (socketRef.current) {
        console.log('Closing WebSocket connection');
        socketRef.current.close();
      }
    };
  }, []);

  const updateMap = (data) => {
    return data.map((item, index) => {
      const { latitude, longitude } = item.data_series_id.location;
      let popupContent = `<strong>${item.data_series_id.name}</strong><br>No relevant data available.`;

      if (item.data_series_id.name.startsWith('Weather')) {
        popupContent = `
          <strong>${item.data_series_id.name}</strong><br>
          Wind Speed: ${item.data.windSpeed || 'N/A'}<br>
          Wind Gusts: ${item.data.windGusts || 'N/A'}<br>
          Precipitation: ${item.data.precipitation || 'N/A'}
        `;
      } else if (item.data_series_id.name.startsWith('AirQuality')) {
        popupContent = `
          <strong>${item.data_series_id.name}</strong><br>
          PM10: ${item.data.pm10 || 'N/A'}<br>
          PM2.5: ${item.data.pm25 || 'N/A'}<br>
          Ozone: ${item.data.ozon || 'N/A'}<br>
          NO2: ${item.data.no2 || 'N/A'}
        `;
      }

      return (
        <Marker key={index} position={[latitude, longitude]}>
          <Popup>
            <div dangerouslySetInnerHTML={{ __html: popupContent }} />
          </Popup>
        </Marker>
      );
    });
  };

  return (
    <div>
      <MapContainer center={[46.11929097137821, 14.756128960803503]} zoom={8} style={{ height: '600px', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="Â© OpenStreetMap contributors"
        />
        {updateMap(data)}
      </MapContainer>
      <div id="dataDisplay">
        <h2>Received Data</h2>
        <pre id="dataOutput">{JSON.stringify(data, null, 2)}</pre>
      </div>
    </div>
  );
};

export default WebSocketMap;
